using System.Collections.Generic;
using System.Linq;
using FluentAssertions;
using NUnit.Framework;

namespace CaesarCipherWinFormsApplication.tests;

[TestFixture]
public class Tests
{
    private readonly CaesarCipherLogic cipherLogic = new();

    [TestCase("ТЕСТ")]
    [TestCase("тест")]
    [TestCase("ТеСт")]
    [TestCase("тест12")]
    [TestCase("тест!'$%")]
    [TestCase("тест🙊")]
    [TestCase("異體字те異體字ст")]
    [TestCase("те ст")]
    public void FilterSymbols_should_skip_symbols_not_from_alphabet_and_return_result_with_upper_case(string message)
    {
        cipherLogic.FilterSymbols(message).Should().Be("ТЕСТ");
    }

    [Test]
    public void FilterSymbols_should_accept_En_and_Ru_symbols()
    {
        cipherLogic.FilterSymbols("тестTEST").Should().Be("ТЕСТTEST");
    }
    
    [Test]
    public void FilterSymbols_Ru_should_replace_ё_with_е_and_ReturnResultWithUpperCase()
    {
        cipherLogic.FilterSymbols("ёжик").Should().Be("ЕЖИК");
    }
    
    [Test]
    public void SplitStr_should_return_a_splitted_string()
    {
        cipherLogic.SplitStr("testtesttest",5).Should().Be("testt estte st");
    }
    
    [TestCaseSource(nameof(GetMostRelativeCharData))]
    public void FindCharsFrequency_should_find_chars_frequency_in_the_message(string message, Dictionary<char, double> result)
    {
        cipherLogic.FindCharsFrequency(message).Should().BeEquivalentTo(result);
    }

    private static IEnumerable<TestCaseData> GetMostRelativeCharData()
    {
        yield return new TestCaseData("test", new Dictionary<char, double>
        {
            { 't', 0.5 },
            { 'e', 0.25 },
            { 's', 0.25 }
        });
        yield return new TestCaseData("TtEeSsTt", new Dictionary<char, double>
        {
            { 'T', 0.25 },
            { 't', 0.25 },
            { 'E', 0.125 },
            { 'e', 0.125 },
            { 'S', 0.125 },
            { 's', 0.125 }
        });
    }

    [TestCase]
    public void EncryptMessage_should_correctly_count_a_shift_that_is_greater_than_the_length_of_the_alphabet()
    {
        cipherLogic.EncryptMessage(35, "БИВЕНЬ").Should().Be("ДЛЕИРЯ");
    }
    
    [Test]
    public void DecryptedMessageWithoutKey_should_return_all_the_expended_strings()
    {
        cipherLogic.DecryptedMessageWithoutKey("ШЛЧШ").Message.Take(5).Should().BeEquivalentTo("ОБНО", "ЕШДЕ", "АУЯА", "ИЫЗИ", "ТЕСТ");
    }

